DECLARE @Id INT;
DECLARE cur CURSOR FAST_FORWARD FOR
    SELECT Id FROM TableName;

OPEN cur;
FETCH NEXT FROM cur INTO @Id;

WHILE @@FETCH_STATUS = 0
BEGIN
    -- Logic here
    FETCH NEXT FROM cur INTO @Id;
END

CLOSE cur;
DEALLOCATE cur;

;WITH CteName AS (
    SELECT Col1, Col2 
    FROM TableName
)
SELECT * FROM CteName;

IF OBJECT_ID('tempdb..#Temp') IS NOT NULL DROP TABLE #Temp;
CREATE TABLE #Temp (
    Id INT,
    Name NVARCHAR(100)
);
